import 'package:flutter/material.dart';

void main() {
  runApp(const MyApp());
}

class MyApp extends StatefulWidget {
  const MyApp({super.key});
  @override
  State<MyApp> createState() => _MyAppState();
}

class _MyAppState extends State<MyApp> {
  ThemeMode _themeMode = ThemeMode.system;
  void changeTheme(ThemeMode mode) {
    setState(() {
      _themeMode = mode;
    });
  }
  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      title: 'Flutter Demo',
      theme: ThemeData(
        colorScheme: ColorScheme.fromSeed(seedColor: Colors.deepPurple),
      ),
      darkTheme: ThemeData.dark(),
      themeMode: _themeMode,
      themeAnimationCurve: Curves.easeIn,
      themeAnimationDuration: const Duration(milliseconds: 500),
      home: MyHomePage(title: 'Flutter Demo Home Page',
        themeMode: _themeMode,
        changeTheme: changeTheme,
),
    );
  }
}

class MyHomePage extends StatefulWidget {
  const MyHomePage({super.key, required this.title, required this.themeMode, required this.changeTheme});

  final String title;
  final ThemeMode themeMode;
  final void Function(ThemeMode) changeTheme;

  @override
  State<MyHomePage> createState() => _MyHomePageState();
}

class _MyHomePageState extends State<MyHomePage> with TickerProviderStateMixin {
  int _counter = 0;
  bool isDark = false;
  String imagePath = 'data:image/jpeg;base64,';
  bool isSwitched = false;
  ThemeMode themeMode = ThemeMode.system;
  late AnimationController _controller;
  late Animation<double> _animation;

  @override
  void initState() {
    super.initState();
    if (themeMode == ThemeMode.dark) {
      isDark = true;
    }
     _controller = AnimationController(
        duration: const Duration(seconds: 1), 
        vsync: this,
      );
      _controller.value = 1.0;
      _animation = CurvedAnimation(parent: _controller, curve: Curves.easeIn);
  }
  @override
    void dispose() {
      _controller.dispose();
      super.dispose();
    }
  void _incrementCounter() {
    setState(() {
      _counter++;
    });
  }

  void _toggleimage(bool val) {
    setState(() {
      if (isSwitched) {
        imagePath = 'data:image/jpeg;base64,';
        isSwitched = false;
      } else {
        imagePath = 'https://images.steamusercontent.com/ugc/2056503000594664135/4A6CC88DE96CE4779E62810DCE348F5241999F59/?imw=512&&ima=fit&impolicy=Letterbox&imcolor=%23000000&letterbox=false';
        isSwitched = true;
      }
      _controller.forward(from: 0.0);
    });
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        backgroundColor: Theme.of(context).colorScheme.inversePrimary,
        title: Text(widget.title),
      ),
      body: Center(
        child: Column(
          mainAxisAlignment: MainAxisAlignment.center,
          children: <Widget>[
            const Text('You have pushed the button this many times:'),
            Text(
              '$_counter',
              style: Theme.of(context).textTheme.headlineMedium,
            ),
            SizedBox(height: 10),
            FadeTransition(
              opacity: _animation,
              child: Image.network(
              imagePath,
              width: 300,
              height: 300,
              fit: BoxFit.cover,
            ),
            ),
            ElevatedButton(
              onPressed: () {
                _toggleimage(isSwitched);
              },
              child: const Text('Change Image'),
            ),
            SizedBox(height: 10),
            ElevatedButton(
              onPressed: () {
                if (isDark) {
                  widget.changeTheme(ThemeMode.light);
                  isDark = false;
                } else {
                  widget.changeTheme(ThemeMode.dark);
                  isDark = true;
                }
              },
              child: const Text('Change Theme'),
            ),
          ],
        ),
      ),
      floatingActionButton: FloatingActionButton.extended(
        onPressed: _incrementCounter,
        tooltip: 'Increment',
        label: const Text('Increment'),
      ), 
    );
  }
}
